import { useState } from 'react';
import "../App.css";

function Cart(props) {

	return (
        <div>
            {cart.map((item) => {
                return cartMap.get(item.name) > 0 ? (
                <div className="cartItem">
                    <p>{item.name}, x{cartMap.get(item.name)}</p>
                    <button className="removeButton" onClick={() => removeFromCart({ item })}>x</button>
                </div>
                ) : <div></div>
            })}
        </div>

	)

}

export default Cart;
