import "./App.css";
import { useState, useEffect } from "react";
import bakeryData from "./assets/bakery-data.json";
import BakeryItem from "./components/BakeryItem";

/* makes the image URLs work */
bakeryData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});

function App() {
  const [cart, updateCart] = useState([]);
  const [cartMap, updateCartMap] = useState(new Map())
  const [total, setTotal] = useState(0);
  const [low, setLow] = useState(true);
  const [medium, setMedium] = useState(true);
  const [high, setHigh] = useState(true);

  useEffect(() => {
    bakeryData.forEach(item => updateMap(item.name, 0));
  }, [])

  function addToCart(item){
    let filteredCart = cart.filter(cartItem => cartItem.name !== item.name);
    updateCart([...filteredCart, item]);
    updateMap(item.name, cartMap.get(item.name) + 1)

    setTotal(parseFloat((total + item.price).toFixed(2)))
  }

  function removeFromCart(item){
    setTotal(parseFloat((total - item.item.price).toFixed(2)))
    updateMap(item.item.name, cartMap.get(item.item.name) - 1)
    if(cartMap.get(item.item.name) == 0) {
      updateCart(cart.filter(arr => arr.name !== item.item.name));
    }
  }

  function updateMap(key, value) {
    updateCartMap(map => new Map(map.set(key, value)));
  }

  function toggleLow() {
    if(low == false) {
      setLow(true)
    }
    else {
      setLow(false)
    }
  }

  function toggleMid() {
    if(medium == false) {
      setMedium(true)
    }
    else {
      setMedium(false)
    }
  }

  function toggleHigh() {
    if(high == false) {
      setHigh(true)
    }
    else {
      setHigh(false)
    }
  }

  return (
    <div className="App">
      <h1>My Bakery</h1> 

      <div className="filter">
        <div className="filterText"><h3>Filter by sweetness!</h3></div>

        <div className="filterCheckboxes">
          <div className="filterItem">
            <p>Low</p>
            <input type="checkbox" defaultChecked={low} onClick={toggleLow}></input>
          </div>

          <div className="filterItem">
            <p>Medium</p>
            <input type="checkbox" defaultChecked={medium} onClick={toggleMid}></input>
          </div>

          <div className="filterItem">
            <p>High</p>
            <input type="checkbox" defaultChecked={high} onClick={toggleHigh}></input>
          </div>

        </div>
      </div>


      <div className="content">
        <div className="bakeryContainers">
          {bakeryData.map((item, index) => {
            return 
            <BakeryItem key={index} name={item.name} desc={item.description}
            price={item.price} image={item.image} total={total} click={addToCart}/>
          })}
        </div>


        <div className="cart">
          <h2>Cart</h2>
          {cart.map((item) => {
            return cartMap.get(item.name) > 0 ? (
              <div className="cartItem">
                <p>{item.name}, x{cartMap.get(item.name)}</p>
                <button onClick={() => removeFromCart({item})}>x</button>
              </div>
            ): <div></div>
          })}



          <br/>
          <h3>Total Cost: ${total}</h3>
        </div>
      </div>

    </div>
  );
}

export default App;
