export default function createGetThemeVar(prefix = '') {
  function appendVar(...vars) {
    if (!vars.length) {
      return '';
    }

    return `, var(--${prefix ? `${prefix}-` : ''}${vars[0]}${appendVar(...vars.slice(1))})`;
  }

  const getThemeVar = (field, ...vars) => {
    return `var(--${prefix ? `${prefix}-` : ''}${field}${appendVar(...vars)})`;
  };

  return getThemeVar;
}
