import "./App.css";
import { useState, useEffect } from "react";
import ArtPiece from "./components/ArtPiece";
import bd from "./assets/bakery-data.json";

/* makes the image URLs work */
bd.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});

function App() {

  return (
    <div className="App">
      <h1>SleepKoala727's Portfolio</h1>
      <h2>Hi, I'm SleepyKoala727!</h2>
      <h3>I'm a senior at Brown University studying User Interfaces and User Experiences.  
      </h3>

      <hr></hr>
      <div className="content">
        <ArtPiece name="Personas & Storyboarding"></ArtPiece>
        <ArtPiece name="Iterative Design and Evaluation"></ArtPiece>
        <ArtPiece name="Art Gallery Shop"></ArtPiece>
        <ArtPiece name="Chomp"></ArtPiece>
      </div>

      <div className="artContainers">
        {bd.map((item, index) => {
          <ArtPiece key={index} name={item.name} desc={item.description}
            price={item.price} image={item.image} total={total} click={addToCart} />
        })}
      </div>

    </div>
  );
}

export default App;
